import { createContext } from "svelte";

class BannerState {
    #show = $state<boolean>();

    constructor(show: boolean = true) {
        this.#show = show;
    };  

    get show() {
        return this.#show;
    };

    set show(v) {
        this.#show = v;
    };
};

const [ getContext, setContext ] = createContext<BannerState>();


export function setupContext(show: boolean) {
    return setContext(new BannerState(show));
};

export { 
    getContext 
};

import Banner from "./banner.svelte";
import BannerAction from "./banner-action.svelte";
import BannerClose from "./banner-close.svelte";
import BannerIcon from "./banner-icon.svelte";
import BannerTitle from "./banner-title.svelte";

export {
    Banner,
    BannerAction,
    BannerClose,
    BannerIcon,
    BannerTitle,

    Banner as Root,
    BannerAction as Action,
    BannerClose as Close,
    BannerIcon as Icon,
    BannerTitle as Title
};
