import { createContext } from "svelte";

export type ChoiceboxStateProps = {
    id?: string;
    value: string;
};

class ChoiceboxState {
    #id = $state<string>()
    #value = $state<string>()!;
    
    constructor(props: ChoiceboxStateProps) {
        this.#id = props.id;
        this.#value = props.value;
    };

    get id() {
        return this.#id;
    };

    set id(v) {
        this.#id = v;
    };

    get value() {
        return this.#value;
    };

    set value(v) {
        this.#value = v;
    };
};

const [ getContext, setContext ] = createContext<ChoiceboxState>();


export function setupContext(props: ChoiceboxStateProps) {
    return setContext(new ChoiceboxState(props));
};

export { 
    type ChoiceboxState, 
    getContext 
};

import Choicebox from "./choicebox.svelte";
import ChoiceboxIndicator from "./choicebox-indicator.svelte";
import ChoiceboxItem from "./choicebox-item.svelte";
import ChoiceboxItemDescription from "./choicebox-item-description.svelte";
import ChoiceboxItemHeader from "./choicebox-item-header.svelte";
import ChoiceboxItemSubtitle from "./choicebox-item-subtitle.svelte";
import ChoiceboxItemTitle from "./choicebox-item-title.svelte";

export {
    Choicebox,
    ChoiceboxIndicator,
    ChoiceboxItem,
    ChoiceboxItemDescription,
    ChoiceboxItemHeader,
    ChoiceboxItemSubtitle,
    ChoiceboxItemTitle,

    Choicebox as Root,
    ChoiceboxIndicator as Indicator,
    ChoiceboxItem as Item,
    ChoiceboxItemDescription as ItemDescription,
    ChoiceboxItemHeader as ItemHeader,
    ChoiceboxItemSubtitle as ItemSubtitle,
    ChoiceboxItemTitle as ItemTitle
};
